import React, { Component } from 'react'
import BaseForm from './../../components/BaseForm/index'
import axios from './../../../src/axios/index'
import { Select, Input,Modal,message,Card,Button,Table,Form ,Icon} from 'antd';
import Utils from './../../utils/utils'
import CpChannelBindAddForm from './add';
const Option = Select.Option;
const FormItem =Form.Item
export default class CpChannelBind extends Component {


    state = {
        list: [],
        isVisible: false
    }

    params = {
        page: 1,
        pageSize: 20
    }


    componentDidMount() {
        this.requestList();
        this.requestSelect();
    }

    requestSelect = () => {
        this.getCodeTypeList();
        this.getMerchantList();
        this.getAppIdList();
    }
    //获取codeType信息
    getCodeTypeList = () => {
        let _this = this;
        axios.requestSelect(_this, '/common/getcodetyelist', {}, true, 'codeTypeList');
    }
    //获取商户信息
    getMerchantList = () => {
        let _this = this;
        axios.requestSelect(_this, '/common/getmerchantlist', {}, true, 'merchantList');
    }
    //获取AppidList
    getAppIdList = () => {
        let _this = this;
        axios.requestSelect(_this, '/common/getappidlist', {}, true, 'appIdList');
    }

    requestList = () => {
        let _this = this;
        axios.requestList(_this, '/cpchannelbind/getcpchannelbindlist', this.params, true);
    }

    handleOperate = (type) => {
        let items = this.state.selectedRows;
        if (type) {
            if (type == 'edit') {
                if (!items || items.length == 0) {
                    Modal.info({
                        title: "提示",
                        content: '请选择一个CP绑定通道信息!'
                    })
                    return;
                }
                if (items.length > 1) {
                    Modal.info({
                        title: "提示",
                        content: '只能选择一个CP绑定通道信息编辑!'
                    })
                    return;
                }

                this.setState({
                    type,
                    isVisible: true,
                    title: '编辑CP绑定通道信息',
                    cpChannelBind: items[0]
                })


                //显示编辑页面
            } else if (type == 'delete') {
                if (items && items.length >= 1) {
                    let _this = this;
                    let ids = '';
                    items.map((item) => {
                        ids += (item.id) + '-';
                    })
                    Modal.confirm({
                        title: '确认删除',
                        content: '是否要删除选中项',
                        onOk() {
                            axios.ajax({
                                url: '/cpchannelbind/delete',
                                data: {
                                    params: {
                                        ids: ids
                                    }
                                }
                            }).then((res) => {
                                if (res.code == 0) {
                                    _this.setState({
                                        isVisible: false,
                                        selectedRowKeys: null
                                    })
                                    message.success("删除成功!");
                                    _this.requestList();

                                } else {
                                    message.success("删除失败!");
                                }

                            })
                        }

                    })

                } else {
                    Modal.info({
                        title: "提示",
                        content: '请选择一个CP通道绑定信息!'
                    })
                    return;
                }
            } else if (type == 'create') {
                this.setState({
                    type,
                    isVisible: true,
                    title: '创建CP通道绑定',
                    merchantInfo: null
                }
                )
            }
        }

    }

    //接收baseForm 组件表单中的查询参数
    handleFilter = (params) => {
        this.params.merchantNo = params.merchantNo;
        this.params.merchantName = params.merchantName;
        this.params.codeType = params.codeType;
        this.params.businessName = params.businessName;
        this.params.realType = params.realType;
        this.requestList();
    }

    // 创建/编辑cp绑定信息提交
    handleSubmit = () => {
        let type = this.state.type;
        this.cpChannelBindAddForm.props.form.validateFields((err, values) => {
            if (!err) {
                let data = this.cpChannelBindAddForm.props.form.getFieldsValue();
                let info = type == 'create' ? '新增' : '编辑';
                axios.ajax({
                    url: type == 'create' ? '/cpchannelbind/add' : '/cpchannelbind/edit',
                    data: {
                        params: data
                    }
                }).then((res) => {
                    if (res.code == 0) {
                        this.cpChannelBindForm.props.form.resetFields();
                        this.setState({
                            isVisible: false,
                            selectedRowKeys: null
                        })
                        message.success(info + "成功!");
                        this.requestList();

                    } else {
                        message.error(info + "失败! " + res.msg);
                    }
                })
            }
        });

    }


    render() {
        const formList = [
            {
                type: 'INPUT',
                label: '商户号',
                field: 'merchantNo',
                width: 200,
                placeholder: '请输入查询的商户号'
            }, {
                type: 'INPUT',
                label: '商户名称',
                field: 'merchantName',
                width: 200,
                placeholder: '请输入查询的商户名称'
            }
            , {
                type: 'INPUT',
                label: 'codeType',
                field: 'codeType',
                width: 200,
                placeholder: '请输入查询的codeType'
            }, {
                type: 'INPUT',
                label: '业务名称',
                field: 'businessName',
                width: 200,
                placeholder: '请输入查询的业务名称'
            }
            , {
                type: 'INPUT',
                label: '真实类型',
                field: 'realType',
                width: 200,
                placeholder: '请输入查询的真实类型'
            }
        ]

        //设置列名
        const columns = [{
            title: 'Id',
            dataIndex: 'id',
            width: 80
        }, {
            title: '商户号',
            dataIndex: 'merchantNo',
            width: 80
        }, {
            title: '商户名称',
            dataIndex: 'merchantName',
            width: 120
        }, {
            title: 'appId',
            dataIndex: 'appId',
            width: 80
        }, {
            title: 'codeType',
            dataIndex: 'codeType',
            width: 80
        }, {
            title: 'businessName',
            dataIndex: 'businessName',
            width: 100
        }, {
            title: 'realType',
            dataIndex: 'realType',
            width: 100
        } 

    ];
        //最前面的选择框
        const selectedRowKeys = this.state.selectedRowKeys;

        const rowSelection = {
            type: 'checkbox',
            selectedRowKeys,
            columnWidth: 5,
            onChange: (selectedRowKeys, selectedRows) => {
                this.setState({
                    selectedRowKeys,
                    selectedRows
                })
            }
        }

        return (
            <div> <Card >
                <BaseForm formList={formList} filterSubmit={this.handleFilter} />
            </Card>

                <Card className='operate-wrap'>
                    <Button type='primary' icon='plus' onClick={() => this.handleOperate('create')}>新增绑定</Button>
                    <Button type='primary' icon='edit' onClick={() => this.handleOperate('edit')}>编辑绑定</Button>
                    <Button type='primary' icon='delete' onClick={() => this.handleOperate('delete')}>删除绑定</Button>
                </Card>
                <div className="content-wrap">
                    <Table
                        bordered
                        size='small'
                        columns={columns}
                        rowSelection={rowSelection}
                        pagination={this.state.pagination}
                        dataSource={this.state.list}

                    />

                </div>
                <Modal
                    title={this.state.title}
                    visible={this.state.isVisible}
                    onOk={this.handleSubmit}
                    onCancel={() => {
                        this.cpChannelBindAddForm.props.form.resetFields();
                        this.setState({
                            isVisible: false
                        })
                    }}
                    width={1000}
                    >

                    <CpChannelBindAddForm type={this.state.type} 
                    appIdList={this.state.appIdList}
                    codeTypeList={this.state.codeTypeList} 
                    merchantList={this.state.merchantList}
                    wrappedComponentRef={(inst) => { this.cpChannelBindAddForm = inst; }} />
                </Modal>

            </div>
        );
    }
}
