import { Row, Col } from 'antd';
import './header.less';
import axios from '../../axios'
import Util from '../../utils/utils';
export default class Header extends Component {
    state = {}
    componentWillMount() {
        setInterval(() => {
            this.setState({
                sysTime: new Date().toLocaleTimeString()
            })
        }, 1000)
        this.setState({
            userName: '王浩洁'
        });
        this.getWeatherAPIData();
    }

