import React, { Component } from 'react'
import { Button, Card, Table, Modal, Form, Input } from 'antd';
import axios from './../../axios/index'
const FormItem = Form.Item;
export default class Permission extends Component {
    state = {

    }


    componentDidMount() {
        this.requestList();
    }
    requestList = () => {
        axios.requestList(this, '/role/list', {});
    }
    handlePermission = () => {
        this.setState({
            isPermissionVisible: true
        })
    }
    handleRole = () => {
        this.setState({
            isRoleVisible: true
        })
    }
    handleAuth = () => {
        this.setState({
            isAuthVisible: true
        })
    }
    render() {
        const selectedRowKeys = this.state.selectedRowKeys;

        const rowSelection = {
            type: 'checkbox',
            selectedRowKeys,
            columnWidth: 5,
            onChange: (selectedRowKeys, selectedRows) => {
                this.setState({
                    selectedRowKeys,
                    selectedRows
                })
            }
        }
        const columns = [
            {
                title: '角色ID',
                dataIndex: 'id'
            }, {
                title: '角色名称',
                dataIndex: 'roleName'
            }, {
                title: '状态',
                dataIndex: 'status',
                render(status) {
                    return status == 1 ? '启用' : '停用'
                }
            }, {
                title: '授权时间',
                dataIndex: 'authorizeTime'
            }, {
                title: '授权人',
                dataIndex: 'authorizeUserName'
            }, {
                title: '创建时间',
                dataIndex: 'createTime'
            }, {
                title: '更新时间',
                dataIndex: 'updateTime'
            },

        ]

        return (
            <div>
                <Card className='operate-wrap'>
                    <Button type='primary' onClick={this.handleRole} >创建角色</Button>
                    <Button type='primary' onClick={this.handlePermission} >设置权限</Button>
                    <Button type='primary' onClick={this.handleAuth}  >用户授权</Button>
                    <Button type='primary' onClick={this.handleRole} >删除角色</Button>
                </Card>
                <div className="content-wrap">
                    <Table
                        bordered
                        size='small'
                        columns={columns}
                        rowSelection={rowSelection}
                        pagination={this.state.pagination}
                        dataSource={this.state.list}
                    />
                </div>
                <Modal
                    title='创建角色'
                    visible={this.state.isRoleVisible}
                    onOk={this.handleRoleSubmit}
                    onCancel={() => {
                        this.roleForm.props.form.resetFields();
                        this.setState({
                            isRoleVisible: false
                        })
                    }}
                    width={600} >
                    <RoleForm roleInfo={this.state.roleInfo} wrappedComponentRef={(inst) => { this.roleForm = inst; }} />
                </Modal>

                <Modal
                    title='设置权限'
                    visible={this.state.isPermissionVisible}
                    onOk={this.handlePermissionSubmit}
                    onCancel={() => {
                        this.permissionForm.props.form.resetFields();
                        this.setState({
                            isPermissionVisible: false
                        })
                    }}
                    width={600} >
                    <PermissionForm permissionInfo={this.state.permissionInfo} wrappedComponentRef={(inst) => { this.permissionForm = inst; }} />
                </Modal>


                <Modal
                    title='用户授权'
                    visible={this.state.isAuthVisible}
                    onOk={this.handleAuthSubmit}
                    onCancel={() => {
                        this.authForm.props.form.resetFields();
                        this.setState({
                            isAuthVisible: false
                        })
                    }}
                    width={600} >
                    <AuthForm authInfo={this.state.authInfo} wrappedComponentRef={(inst) => { this.authForm = inst; }} />
                </Modal>

            </div>

        );
    }
}

class RoleForm extends Component {


    render() {
        const { getFieldDecorator } = this.props.form;
        const formItemLayout = {
            labelCol: { span: 5 },
            wrapperCol: { span: 19 }
        }

        return (

            <Form layout="horizontal">
                <FormItem label="角色名称" {...formItemLayout}>
                    {

                        getFieldDecorator('roleName', {
                            initialValue: roleInfo.roleName,
                            rules: [{ required: true, message: '请输入一个角色名称' },]
                        })(
                                <Input type="text" placeholder=''></Input>
                        )
                    }
                </FormItem>
                <FormItem label="状态" {...formItemLayout}>
                    {
                        type == 'status' ? dsInfo.dsType :
                            getFieldDecorator('status', {
                                initialValue: roleInfo.status?roleInfo.status:'1',
                                rules: [{ required: true, message: '请选择一个状态!' }]
                            })(
                                <Select>
                                    <Option value={1} >生效</Option>
                                    <Option value={0}>失效</Option>
                                </Select>
                            )
                    }
                </FormItem>


            </Form>
        );
    }


}

Form.create({})(RoleForm);


class PermissionForm extends Component {
    render() {
        return (
            <div>

            </div>
        );
    }
}

Form.create({})(PermissionForm);

class AuthForm extends Component {
    render() {
        return (
            <div>

            </div>
        );
    }
}

Form.create({})(AuthForm);
