import React, { Component } from 'react'
import './index.less'
import FormItem from 'antd/lib/form/FormItem';
import { Card, message, Form, Input, Button, Table, Icon, Divider, Modal } from 'antd'
import BaseForm from './../../components/BaseForm/'
import axios from '../../axios'
export default class CpManage extends Component {

    state = {
        list: [],

        isVisible: false
    }
    params = {
        page: 1
    }

    handleOperate = (type) => {
        let item =this.state.selectedRows;
        console.log(item);
        if (type) {
            if (type == 'edit') {
                if(!item){
                    Modal.info({
                        title: "提示",
                        content: '请选择一个商户'
                    })
                    return;   
                }


            } else if (type == 'delete') {

            } else if (type == 'create') {
                this.setState({
                    type,
                    isVisible: true,
                    title: '创建商户'
                }
            )
            } else if (type == 'batchDelete') {

            }
        }

    }

    //接收baseForm 组件表单中的查询参数
    handleFilter = (params) => {
        this.params = params;
        this.requestList();
    }
    //删除商户
    handleDelete = (item) => {
        let id = item.id;
        Modal.confirm({
            title: '确定',
            content: '您确认要删除这条数据吗？',
            onOk: () => {
                //请求接口删除
                message.success("删除成功!");

            }
        })

    }

    componentDidMount() {
        this.requestList();
    }

    requestList = () => {
        let _this = this;
        axios.requestList(_this, '/getMerchantList', this.params, true);
    }

    render() {
        const formList = [
            {
                type: 'INPUT',
                label: '商户号',
                field: 'merchantNo',
                width: 200,
                placeholder: '请输入查询的商户号'
            }, {
                type: 'INPUT',
                label: '商户名称',
                field: 'merchantName',
                width: 200,
                placeholder: '请输入查询的商户名称'
            }
        ]

        //设置列名
        const columns = [{
            title: 'Id',
            dataIndex: 'id',
            width: 80
        }, {
            title: '商户号',
            dataIndex: 'merchantNo',
            width: 80
        }, {
            title: '商户名称',
            dataIndex: 'merhantName',
            width: 120
        }, {
            title: '密码',
            dataIndex: 'merchantPwd',
            width: 80
        }];
        const selectedRowKeys = this.state.selectedRowKeys;

        const rowSelection = {
            type: 'checkbox',
            selectedRowKeys,
            columnWidth: 5,
            onChange: (selectedRowKeys, selectedRows) => {
                this.setState({
                    selectedRowKeys,
                    selectedRows
                })
            }
        }

        return (
            <div>
                <Card >
                    <BaseForm formList={formList} filterSubmit={this.handleFilter} />
                </Card>

                <Card>
                    <Button type='primary' icon='plus' onClick={() => this.handleOperate('create')}>新增商户</Button>
                    <Button type='primary' style={{ marginLeft: 10 }} icon='edit' onClick={() => this.handleOperate('edit')}>编辑商户</Button>
                    <Button type='primary' style={{ marginLeft: 10 }} icon='delete' onClick={() => this.handleOperate('delete')}>删除商户</Button>
                </Card>
                <div className="content-wrap">
                    <Table
                        bordered
                        size='small'
                        scroll={{ y: 240 }}
                        columns={columns}
                        rowSelection={rowSelection}
                        pagination={this.state.pagination}
                        dataSource={this.state.list}
                    />
                </div>
            </div>
        );
    }

}
