import React, { Component } from 'react'
import './index.less'
import FormItem from 'antd/lib/form/FormItem';
import { Card, message, Form, Input, Button, Table, Icon, Divider, Modal } from 'antd'
import BaseForm from './../../components/BaseForm/'
import axios from '../../axios'
export default class DsManage extends Component {

    state = {
        list: [],
        isVisible: false
    }
    params = {
        page: 1,
        pageSize: 20
    }


    handleOperate = (type) => {
        let items = this.state.selectedRows;
        if (type) {
            if (type == 'edit') {
                if (!items || items.length == 0) {
                    Modal.info({
                        title: "提示",
                        content: '请选择一个下行'
                    })
                    return;
                }
                if (items.length > 1) {
                    Modal.info({
                        title: "提示",
                        content: '只能选择一个下行编辑'
                    })
                    return;
                }

                this.setState({
                    type,
                    isVisible: true,
                    title: '编辑下行',
                    dsInfo: items[0]
                })

            } else if (type == 'delete') {
                if (items && items.length >= 1) {
                    //删除下行
                    let _this = this;
                    let ids = '';
                    items.map((item) => {
                        ids += (item.id) + '-';
                    })
                    Modal.confirm({
                        title: '确认删除',
                        content: '是否要删除选中项',
                        onOk() {
                            axios.ajax({
                                url: '/cpmanage/delete',
                                data: {
                                    params: {
                                        ids: ids
                                    }
                                }
                            }).then((res) => {
                                if (res.code == 0) {
                                    _this.setState({
                                        isVisible: false,
                                        selectedRowKeys: null
                                    })
                                    message.success("删除成功!");
                                    _this.requestList();

                                } else {
                                    message.success("删除失败!");
                                }

                            })
                        }

                    })

                } else {
                    Modal.info({
                        title: "提示",
                        content: '请选择一个商户'
                    })
                    return;
                }
            } else if (type == 'create') {
                this.setState({
                    type,
                    isVisible: true,
                    title: '创建商户',
                    dsInfo: null
                }
                )
            }
        }

    }

    //接收baseForm 组件表单中的查询参数
    handleFilter = (params) => {
        this.params.businessName = params.businessName;
        this.params.dsType = params.dsType;
        this.params.dsPort = params.dsPort;
        this.requestList();
    }

    // 创建/编辑商户提交
    handleSubmit = () => {
        let type = this.state.type;
        this.merchantForm.props.form.validateFields((err, values) => {
            if (!err) {
                let data = this.dsForm.props.form.getFieldsValue();
                let info = type == 'create' ? '新增' : '编辑';
                axios.ajax({
                    url: type == 'create' ? '/cpmanage/add' : '/cpmanage/edit',
                    data: {
                        params: data
                    }
                }).then((res) => {
                    if (res.code == 0) {
                        this.merchantForm.props.form.resetFields();
                        this.setState({
                            isVisible: false,
                            selectedRowKeys: null
                        })
                        message.success(info + "成功!");
                        this.requestList();

                    } else {
                        message.error(info + "失败! " + res.msg);
                    }
                })
            }
        });

    }

    //获取codeType
    getCodeType = () => {
        axios.requestSelect(_this, '/cpchannelbind/getcodetye', {}, true, 'codeType');
    }


    componentDidMount() {
        this.requestList();
    }

    requestList = () => {
        let _this = this;
        axios.requestList(_this, '/dsmanage/getdslist', this.params, true);
    }

    render() {
        const formList = [
            {
                type: 'INPUT',
                label: '业务名称',
                field: 'businessName',
                width: 200,
                placeholder: '请输入业务名称'
            }, {
                type: 'SELECT',
                label: '下行类型',
                field: 'dsType',
                width: 200,
                list: [{ id: '-1', name: '请选择' }, { id: '1', name: '二次确认' }, { id: '2', name: '验证码' }, { id: '3', name: '订购成功下行' }]
            }
            , {
                type: 'INPUT',
                label: '端口号',
                field: 'dsPort',
                width: 200,
                placeholder: '请输入端口号'
            }
        ]

        //设置列名
        const columns = [{
            title: 'Id',
            dataIndex: 'id',
            width: 80
        }, {
            title: 'codeType',
            dataIndex: 'codeType',
            width: 80
        },
        {
            title: '业务名称',
            dataIndex: 'businessName',
            width: 80
        }, {
            title: '下行类型',
            dataIndex: 'dsType',
            width: 80,
            render(dsType) {
                return {
                    '1': '二次确认',
                    '2': '验证码',
                    '3': '订购成功下行'
                }[dsType]

            }
        }, {
            title: '端口号',
            dataIndex: 'dsPort',
            width: 80
        }
            , {
            title: '内容',
            dataIndex: 'dsContent',
            width: 120

        }, {
            title: '创建时间',
            dataIndex: 'createTime',
            width: 80
        }, {
            title: '更新时间',
            dataIndex: 'updateTime',
            width: 80
        }

        ];
        const selectedRowKeys = this.state.selectedRowKeys;

        const rowSelection = {
            type: 'checkbox',
            selectedRowKeys,
            columnWidth: 5,
            onChange: (selectedRowKeys, selectedRows) => {
                this.setState({
                    selectedRowKeys,
                    selectedRows
                })
            }
        }

        return (
            <div>
                <Card >
                    <BaseForm formList={formList} filterSubmit={this.handleFilter} />
                </Card>

                <Card className='operate-wrap'>
                    <Button type='primary' icon='plus' onClick={() => this.handleOperate('create')}>新增下行</Button>
                    <Button type='primary' icon='edit' onClick={() => this.handleOperate('edit')}>编辑下行</Button>
                    <Button type='primary' icon='delete' onClick={() => this.handleOperate('delete')}>删除下行</Button>
                </Card>
                <div className="content-wrap">
                    <Table
                        bordered
                        size='small'
                        scroll={{ y: 240 }}
                        columns={columns}
                        rowSelection={rowSelection}
                        pagination={this.state.pagination}
                        dataSource={this.state.list}
                    />
                </div>
                <Modal
                    title={this.state.title}
                    visible={this.state.isVisible}
                    onOk={this.handleSubmit}
                    onCancel={() => {
                        this.dsForm.props.form.resetFields();
                        this.setState({
                            isVisible: false
                        })
                    }}
                    width={600}
                >
                    <DSForm type={this.state.type} dsInfo={this.state.dsInfo} wrappedComponentRef={(inst) => { this.dsForm = inst; }} />
                </Modal>

            </div>
        );
    }

}
class DSForm extends React.Component {

    render() {
        let type = this.props.type;
        let dsInfo = this.props.dsInfo || {};
        const { getFieldDecorator } = this.props.form;
        const formItemLayout = {
            labelCol: { span: 5 },
            wrapperCol: { span: 19 }
        }
        return (
            <Form layout="horizontal">
                <FormItem label="codeType" {...formItemLayout}>
                    {
                        type == 'detail' ? dsInfo.codeType :
                            getFieldDecorator('codeType', {
                                initialValue: dsInfo.codeType,
                                rules: [{ required: true, message: '请输入商户号!' },]
                            })(
                                <Input type="text" placeholder="请输入商户号" />
                            )
                    }
                </FormItem>
                <FormItem label="商户名称" {...formItemLayout}>
                    {
                        type == 'detail' ? dsInfo.merchantName :
                            getFieldDecorator('merchantName', {
                                initialValue: dsInfo.merchantName,
                                rules: [{ required: true, message: '请输入商户名称!' }]
                            })(
                                <Input type="text" placeholder="请输入商户名称" />
                            )
                    }
                </FormItem>
                <FormItem label="商户密码" {...formItemLayout}>
                    {
                        type == 'detail' ? dsInfo.merchantPwd :
                            getFieldDecorator('merchantPwd', {
                                initialValue: dsInfo.merchantPwd,
                                rules: [{ required: true, message: '请输入商户密码!' }, {
                                    min: 5, max: 10, message: "商户密码长度不符合[5-10位]!"
                                }]
                            })(
                                <Input type="text" placeholder="请输入商户密码" />
                            )
                    }
                </FormItem>
            </Form>
        );
    }
}
DSForm = Form.create({})(DSForm);
