import React, { Component } from 'react'
import { Button, Card, Table, Modal, Form, Input, Select } from 'antd';
import axios from './../../axios/index'
const Option = Select.Option;
const FormItem = Form.Item;
export default class Permission extends Component {
    state = {

    }


    componentDidMount() {
        this.requestList();
    }
    requestList = () => {
        axios.requestList(this, '/role/list', {});
    }
    handlePermission = () => {
        this.setState({
            isPermissionVisible: true
        })
    }

    handleRoleSubmit=()=>{
        let type = this.state.roleType;
        this.roleForm.props.form.validateFields((err, values) => {
            if (!err) {
                let data = this.roleForm.props.form.getFieldsValue();
                let info = type == 'create' ? '新增' : '编辑';
                axios.ajax_post({
                    url: type == 'create' ? '/role/add' : '/role/edit',
                    data: {
                        params: data
                    }
                }).then((res) => {
                    if (res.code == 0) {
                        this.roleForm.props.form.resetFields();
                        this.setState({
                            isRoleVisible: false,
                            selectedRowKeys: null
                        })
                        message.success(info + "成功!");
                        this.requestList();
                    } else {
                        message.error(info + "失败! " + res.msg);
                    }
                })
            }
        });

    }

    handleRole = (type) => {
        let items = this.state.selectedRows;
        if (type) {
            if (type == 'edit') {
                if (!items || items.length == 0) {
                    Modal.info({
                        title: "提示",
                        content: '请选择一个角色'
                    })
                    return;
                }
                if (items.length > 1) {
                    Modal.info({
                        title: "提示",
                        content: '只能选择一个角色编辑'
                    })
                    return;
                }

                this.setState({
                    roleType:type,
                    isRoleVisible: true,
                    roleTitle: '编辑角色',
                    roleInfo: items[0]
                })

            } else if (type == 'delete') {
                if (items && items.length >= 1) {
                    //删除角色
                    let _this = this;
                    let ids = [];
                    items.map((item) => {
                        ids .push(item.id);
                    })
                    Modal.confirm({
                        title: '确认删除',
                        content: '是否要删除选中项',
                        onOk() {
                            axios.ajax_post({
                                url: '/role/delete',
                                data: {
                                    params: {
                                        ids: ids
                                    }
                                }
                            }).then((res) => {
                                if (res.code == 0) {
                                    _this.setState({
                                        isVisible: false,
                                        selectedRowKeys: null
                                    })
                                    message.success("删除成功!");
                                    _this.requestList();

                                } else {
                                    message.success("删除失败!");
                                }

                            })
                        }

                    })

                } else {
                    Modal.info({
                        title: "提示",
                        content: '请选择一个角色'
                    })
                    return;
                }
            } else if (type == 'create') {
                this.setState({
                    roleType:type,
                    isRoleVisible: true,
                    roleTitle: '创建角色',
                    roleInfo: null
                }
                )
            }
        }

    }

    handleAuth = () => {
        this.setState({
            isAuthVisible: true
        })
    }
    render() {
        const selectedRowKeys = this.state.selectedRowKeys;

        const rowSelection = {
            type: 'checkbox',
            selectedRowKeys,
            columnWidth: 5,
            onChange: (selectedRowKeys, selectedRows) => {
                this.setState({
                    selectedRowKeys,
                    selectedRows
                })
            }
        }
        const columns = [
            {
                title: '角色ID',
                dataIndex: 'id'
            }, {
                title: '角色名称',
                dataIndex: 'roleName'
            }, {
                title: '状态',
                dataIndex: 'status',
                render(status) {
                    return status == 1 ? '启用' : '停用'
                }
            }, {
                title: '授权时间',
                dataIndex: 'authorizeTime'
            }, {
                title: '授权人',
                dataIndex: 'authorizeUserName'
            }, {
                title: '创建时间',
                dataIndex: 'createTime'
            }, {
                title: '更新时间',
                dataIndex: 'updateTime'
            },

        ]

        return (
            <div>
                <Card className='operate-wrap'>
                    <Button type='primary' onClick={()=>this.handleRole('create')} >创建角色</Button>
                    <Button type='primary' onClick={()=>this.handleRole('edit')} >编辑角色</Button>
                    <Button type='primary' onClick={()=>this.handleRole('delete')} >删除角色</Button>
                    <Button type='primary' onClick={this.handlePermission} >设置权限</Button>
                    <Button type='primary' onClick={this.handleAuth}  >用户授权</Button>

                </Card>
                <div className="content-wrap">
                    <Table
                        bordered
                        size='small'
                        columns={columns}
                        rowSelection={rowSelection}
                        pagination={this.state.pagination}
                        dataSource={this.state.list}
                    />
                </div>
                <Modal
                    title={this.state.roleTitle}
                    visible={this.state.isRoleVisible}
                    onOk={this.handleRoleSubmit}
                    onCancel={() => {
                        this.roleForm.props.form.resetFields();
                        this.setState({
                            isRoleVisible: false
                        })
                    }}
                    width={600} >
                    <RoleForm roleInfo={this.state.roleInfo} wrappedComponentRef={(inst) => { this.roleForm = inst; }} />
                </Modal>

                <Modal
                    title='设置权限'
                    visible={this.state.isPermissionVisible}
                    onOk={this.handlePermissionSubmit}
                    onCancel={() => {
                        this.permissionForm.props.form.resetFields();
                        this.setState({
                            isPermissionVisible: false
                        })
                    }}
                    width={600} >
                    <PermissionForm permissionInfo={this.state.permissionInfo} wrappedComponentRef={(inst) => { this.permissionForm = inst; }} />
                </Modal>


                <Modal
                    title='用户授权'
                    visible={this.state.isAuthVisible}
                    onOk={this.handleAuthSubmit}
                    onCancel={() => {
                        this.authForm.props.form.resetFields();
                        this.setState({
                            isAuthVisible: false
                        })
                    }}
                    width={600} >
                    <AuthForm authInfo={this.state.authInfo} wrappedComponentRef={(inst) => { this.authForm = inst; }} />
                </Modal>

            </div>

        );
    }
}

class RoleForm extends Component {



    render() {

        const { getFieldDecorator } = this.props.form;
        const formItemLayout = {
            labelCol: { span: 5 },
            wrapperCol: { span: 19 }
        }
        const roleInfo = this.props.roleInfo || {};
        return (
            <Form layout="horizontal">
                <FormItem >
                    {
                        getFieldDecorator('id', {
                            initialValue: roleInfo.id
                        })(
                            <Input hidden type="text" ></Input>
                        )
                    }
                </FormItem>
                <FormItem label="角色名称" {...formItemLayout}>
                    {

                        getFieldDecorator('roleName', {
                            initialValue: roleInfo.roleName,
                            rules: [{ required: true, message: '请输入一个角色名称!' },]
                        })(
                            <Input type="text" placeholder='请输入一个角色名称'></Input>
                        )
                    }
                </FormItem>
                <FormItem label="状态" {...formItemLayout}>
                    {
                        getFieldDecorator('status', {
                            initialValue: roleInfo.status ? roleInfo.status : '1',
                            rules: [{ required: true, message: '请选择一个状态!' }]
                        })(
                            <Select>
                                <Option value="1">生效</Option>
                                <Option value="0">失效</Option>
                            </Select>
                        )
                    }
                </FormItem>
            </Form>
        );
    }


}

RoleForm= Form.create({})(RoleForm);


class PermissionForm extends Component {
    render() {
        return (
            <div>

            </div>
        );
    }
}

Form.create({})(PermissionForm);

class AuthForm extends Component {
    render() {
        return (
            <div>

            </div>
        );
    }
}

Form.create({})(AuthForm);
