import React, { Component } from 'react'
import './index.less'
import FormItem from 'antd/lib/form/FormItem';
import { Card, message, Form, Input, Button, Table, Icon, Divider, Modal } from 'antd'
import BaseForm from './../../components/BaseForm/'
import axios from '../../axios'
export default class CpManage extends Component {

    state = {
        list: [],

        isVisible: false
    }
    params = {
        page: 1
    }

    handleOperate = (type) => {
        if (type) {
            if (type == 'edit') {

            } else if (type == 'delete') {

            } else if (type == 'create') {
                this.setState({
                    type,
                    isVisible:true,
                    title:'创建商户'
                })

            }
        }

    }


    //接收baseForm 组件表单中的查询参数
    handleFilter = (params) => {
        this.params = params;
        this.requestList();
    }
    //删除商户
    handleDelete = (item) => {
        let id = item.id;
        Modal.confirm({
            title: '确定',
            content: '您确认要删除这条数据吗？',
            onOk: () => {
                //请求接口删除
                message.success("删除成功!");

            }
        })

    }

    componentDidMount() {
        this.requestList();
    }

    requestList = () => {
        let _this = this;
        axios.requestList(_this, '/getMerchantList', this.params, true);
    }

    render() {
        const formList = [
            {
                type: 'INPUT',
                label: '商户号',
                field: 'merchantNo',
                width: 200,
                placeholder: '请输入查询的商户号'
            }, {
                type: 'INPUT',
                label: '商户名称',
                field: 'merchantName',
                width: 200,
                placeholder: '请输入查询的商户名称'
            }
        ]
        //设置列名
        const columns = [{
            title: 'Id',
            dataIndex: 'id'
        }, {
            title: '商户号',
            dataIndex: 'merchantNo'
        }, {
            title: '商户名称',
            dataIndex: 'merhantName'
        }, {
            title: '密码',
            dataIndex: 'merchantPwd'
        }, {
            title: '操作',
            render: (text, record) => (
                <div>

                    <Icon type='edit' onClick={() => this.handleOperate('edit')}  ></Icon>
                    <Divider type="vertical" />
                    <Icon type='delete' onClick={() => this.handleOperate('delete')}></Icon>
                </div>
            )
        }];



        return (
            <div>
                <Card >
                    <BaseForm formList={formList} filterSubmit={this.handleFilter} />
                </Card>

                <Card>
                    <Button type='primary' icon='plus' onClick={()=>this.handleOperate('create')}>新增商户</Button>
                </Card>
                <div className="content-wrap">
                <Table
                    bordered
                    size='small'
                    columns={columns}
                    pagination={this.state.pagination}
                    dataSource={this.state.list}
                />
                </div>
            </div>
        );
    }

}
