import React, { Component } from 'react'
import FormItem from 'antd/lib/form/FormItem';
import { Card, message, Form, Input, Button } from 'antd'
export default class CpManage extends Component {


    render() {
        return (
            <div>
                <Card >
                    <FilterForm />

                </Card>


                <Card>
                    <Button type='primary'>新增商户</Button>
                </Card>
                <div className="content-wrap">
                    <Table
                        bordered
                        columns={columns}
                        dataSource={this.state.list}
                        pagination={this.state.pagination}
                    />
                </div>

            </div>
        );
    }

}

class FilterForm extends Component {

    handleReset = () => {
        this.props.form.resetFields();
    }
    handleSubmit = () => {

    }

    render() {
        const {
            getFieldDecorator
        } = this.props.form;
        return (

            <Form layout="inline" onSubmit={this.handleSubmit}>
                <FormItem label='商户号'>
                    {getFieldDecorator('merchantNo')(
                        <Input placeholder="请输入查询的商户号" />
                    )}
                </FormItem>
                <FormItem label='商户名称'>
                    {getFieldDecorator('merchantName')(
                        <Input placeholder="请输入查询的商户名称" />
                    )}
                </FormItem>

                <FormItem>
                    <Button onClick={this.handleSubmit} type='primary' style={{ margin: '0 20px' }}> 查询</Button>
                    <Button onClick={this.handleReset}>重置</Button>
                </FormItem>

            </Form>);
    }

}
FilterForm = Form.create({})(FilterForm);
