                </span>

            )
        }];



        return (
            <div>
                <Card >
                    <BaseForm formList={formList} filterSubmit={this.handleFilter} />
                </Card>

                <Card>
                    <Button type='primary' onClick={this.handleAddMerchant}>新增商户</Button>
                </Card>

                <Table
                    bordered
                    columns={columns}
                    dataSource={this.state.dataSource}
                />
            </div>
        );
    }

}
