import React, { Component } from 'react'
import FormItem from 'antd/lib/form/FormItem';
import { Card, message, Form, Input, Button, Table, Icon ,Divider} from 'antd'
export default class CpManage extends Component {

    state = {
        dataSource: []
    }
    params = {
        page: 1
    }

      //新增商户
      handleAddMerchant = () => {

    }
    render() {


        //请求接口获取商户信息


        //设置列名

        const columns = [{
            title: 'Id',
            dataIndex: 'id'
        }, {
            title: '商户号',
            dataIndex: 'merchantNo'
        }, {
            title: '商户名称',
            dataIndex: 'address'

        }, {
            title: '密码',
            dataIndex: 'merchantPwd'
        }, {
            title: '操作',
            render: (record) => (
                <span>
                    <Icon tye='edit' ></Icon>
                    <Divider type="vertical" />
                    <Icon tye='detele'></Icon>
                </span>

            )
        }];



        return (
            <div>
                <Card >
                    <FilterForm />
                </Card>

                <Card>
                    <Button type='primary' onClick={this.handleAddMerchant}>新增商户</Button>
                </Card>

                <Table
                    bordered
                    columns={columns}
                    dataSource={this.state.dataSource}
                />
            </div>
        );
    }

}

class FilterForm extends Component {

    handleReset = () => {
        this.props.form.resetFields();
    }
    handleSubmit = () => {

    }

    render() {
        const { getFieldDecorator } = this.props.form;
        return (

            <Form layout="inline" onSubmit={this.handleSubmit}>
                <FormItem label='商户号'>
                    {getFieldDecorator('merchantNo')(
                        <Input placeholder="请输入查询的商户号" />
                    )}
                </FormItem>
                <FormItem label='商户名称'>
                    {getFieldDecorator('merchantName')(
                        <Input placeholder="请输入查询的商户名称" />
                    )}
                </FormItem>

                <FormItem>
                    <Button onClick={this.handleSubmit} type='primary' style={{ margin: '0 20px' }}> 查询</Button>
                    <Button onClick={this.handleReset}>重置</Button>
                </FormItem>

            </Form>);
    }

}
FilterForm = Form.create({})(FilterForm);
